import React from 'react'
import Navbar from './components/navbar/Navbar'
import News from './components/news/News'

/*const App = () => {
  return (
    <>
      <Navbar/>
      <News/>
    </>
  )
}

export default App */

document.addEventListener('DOMContentLoaded', function () {
  const categories = {
      home: 'Welcome to the Home page. Here are the top news articles.',
      about: 'Learn more About us and our mission.',
      general: 'Here are some General news articles.',
      international: 'Read the latest International news.',
      politics: 'Get the latest updates in Politics.',
      sports: 'Catch up on the latest Sports news.'
  };

  function displayNews(category) {
      const newsContent = document.getElementById('newsContent');
      newsContent.innerHTML = `<p>${categories[category]}</p>`;
  }

  const categoryLinks = document.querySelectorAll('.news-category');
  categoryLinks.forEach(link => {
      link.addEventListener('click', function (event) {
          event.preventDefault();
          categoryLinks.forEach(l => l.classList.remove('active'));
          this.classList.add('active');
          const category = this.id;
          displayNews(category);
      });
  });

  // Default to displaying the 'home' category news
  displayNews('home');
});
